      gameMode: mode
    }));
  }, []);

  /**
   * Go back to menu
   */
  const goToMenu = useCallback(() => {
    setGameMode('menu');
  }, [setGameMode]);

  /**
   * Get the winning line for highlighting
   */
  const winningLine = getWinningLine(gameState.board);

  return {
    gameState,
    isAIThinking,
    winningLine,
    makeMove,
    newGame,
    resetGame,
    setGameMode,
    goToMenu,
  };
};

