// Game Types and Interfaces for Tic-Tac-Toe

export type Player = 'X' | 'O';
export type CellValue = Player | null;
export type Board = CellValue[];

export interface GameState {
  board: Board;
  currentPlayer: Player;
  winner: Player | null;
  isDraw: boolean;
  isGameOver: boolean;
  gameMode: GameMode;
  moveCount: number;
}

export type GameMode = 'ai' | 'player' | 'menu';

export interface GameStats {
  gamesPlayed: number;
  wins: number;
  losses: number;
  draws: number;
  winRate: number;
}

export interface Move {
  index: number;
  player: Player;
}

export interface GameConfig {
  aiDifficulty: 'easy' | 'medium' | 'hard';
  firstPlayer: Player;
}

export interface WinningLine {
  indices: number[];
  pattern: 'row' | 'column' | 'diagonal';
}

// AI Types
export interface AIMove {
  index: number;
  score: number;
}

export interface GameContextType {
  gameState: GameState;
  stats: GameStats;
  makeMove: (index: number) => void;
  resetGame: () => void;
  setGameMode: (mode: GameMode) => void;
  newGame: (mode: GameMode) => void;
}

// UI Component Props
export interface SquareProps {
  value: CellValue;
  onClick: () => void;
  isWinning?: boolean;
  disabled?: boolean;
}

export interface BoardProps {
  board: Board;
  onSquareClick: (index: number) => void;
  winningLine?: number[];
  disabled?: boolean;
}

export interface GameInfoProps {
  currentPlayer: Player;
  winner: Player | null;
  isDraw: boolean;
  gameMode: GameMode;
  moveCount: number;
}

export interface GameMenuProps {
  onModeSelect: (mode: GameMode) => void;
  stats: GameStats;
}

export interface StatsDisplayProps {
  stats: GameStats;
}

// Constants
export const INITIAL_BOARD: Board = Array(9).fill(null);

export const WINNING_COMBINATIONS: number[][] = [
  [0, 1, 2], // top row
